mapper.insert:
insert into tbl_Scrap (
					   Member_ID
					   ,Category_ID
					   ,Contents_ID
					   ,Contents_Title
					   ,Contents_Thumbnail
					   ,Created
					   ,Updated
					   )
				values(
						#{memberID}
						,(select Category_ID
						  from tbl_Contents where Contents_ID = #{contentsID})
						,#{contentsID}
						,(select Contents_Title
						  from tbl_Contents where Contents_ID = #{contentsID})
						,"http://cfile26.uf.tistory.com/image/2642053B532A9F7519A004"

						,now()
						,now()
						)

mapper.select:
select Scrap_ID scrapID ,Member_ID memberID ,Category_ID categoryID ,Contents_ID contentsID ,Contents_Title contentsTitle ,Contents_Thumbnail contentsPhoto ,Created created
from tbl_Scrap
where Member_ID = #{memberID}
